import { Story, Language } from '../types';

// Helper function to shuffle arrays for quiz options
const shuffleArray = <T>(array: T[]): T[] => {
  return [...array].sort(() => Math.random() - 0.5);
};

// Helper function to create scrambled words from technical terms
const createWordScrambles = (technicalWords: { [key: string]: string }): { scrambled: string; correct: string; hint: string }[] => {
    return Object.entries(technicalWords).map(([term, definition]) => {
        const cleanTerm = term.replace(/\s*\(.*\)\s*/, '').trim();
        return {
            scrambled: shuffleArray(cleanTerm.split('')).join(''),
            correct: cleanTerm,
            hint: definition,
        };
    });
};

const placeholderContent = (title: string, lang: Language) => {
    const isAr = lang === Language.AR;
    return {
        title: isAr ? `قصة ${title}` : `${title} Story`,
        storyText: [isAr ? "الترجمة العربية لهذه القصة ستكون متاحة قريبا." : `The translation for this story is coming soon.`],
        qcm: [],
        dragDrop: [],
        wordScramble: [],
    };
};

